import React from 'react';
import firebase, { initializeApp } from 'firebase';
// import {Switch, Route, Router} from 'react-router-dom';
import './App.css';
import HomePage from './pages/HomePage';
import LoginPage from './pages/LoginPage';

const location = {
  address: '1600 Amphitheatre Parkway, Mountain View, california.',
  lat: 37.42216,
  lng: -122.08427,
}

function initializeFirebase() {
  const firebaseConfig = {
    apiKey: "AIzaSyDthy2mb-wLYd_Z9SwE6jEuxfEPLKg18RU",
    authDomain: "tree-fam.firebaseapp.com",
    databaseURL: "https://tree-fam.firebaseio.com",
    projectId: "tree-fam",
    storageBucket: "tree-fam.appspot.com",
    messagingSenderId: "1036769347273",
    appId: "1:1036769347273:web:aec075c24f7db61acd6f18",
    measurementId: "G-5C1J8ZJSL9"
  };

  firebase.initializeApp(firebaseConfig);
}

class App extends React.Component {
  constructor() {
    super();
    initializeFirebase();
    const databaseRef = firebase.database();

    this.state = {
      userData: {
        email: "",
        username: "",
        points: "0"
      }, 
      route: "login"
    }
  }

  changeRoute = (route, email, username) => {
    this.setState({
      userData: {
        email: email,
        username: username,
        points: 0
      },
      route: route
    })
  }

  render() {  
    return (
      <div className="App">
        {this.state.route === "login" 
          ? <LoginPage changeRoute = {this.changeRoute} />
          : <HomePage location={location} zoomLevel={17} userData={this.state.userData}/>
        }
      </div>
    );
  }
}

export default App;
